import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHtml5, faCss3, faJs, faReact, faNodeJs } from '@fortawesome/free-brands-svg-icons';

export default function Technology() {

  const technologies = [
    { name: "JavaScript", icon: faJs },
    { name: "HTML", icon: faHtml5 },
    { name: "CSS", icon: faCss3 },
    { name: "React", icon: faReact },
    { name: "Node.js", icon: faNodeJs }
  ];

  return (
    <div className="bg-base-100 about py-20 px-36 flex flex-col items-center">
      {/* Title */}
      <h1 className="text-5xl font-bold p-2 mb-10 bg-primary rounded-lg inline-block">Technologies</h1>

      {/* Two columns layout */}
      <div className="grid grid-cols-2 gap-8 w-full">
        {/* Technologies column */}

        {/* Technologies */}
      <div>
        <h2 className='text-left text-2xl font-bold'>Languages</h2>
        <div className="flex flex-wrap">
          {technologies.map((tech, index) => (
            <div key={index} className="flex items-center justify-center p-3 bg-primary rounded-lg inline-block mb-3 mr-3">
              <FontAwesomeIcon icon={tech.icon} size="2x" className="text-white" />
              <p className="ml-2 text-white">{tech.name}</p>
            </div>
          ))}
        </div>
      </div>

        {/* Code mockup column */}
        <div className="mockup-code rounded-tr-none rounded-br-none bg-primary text-primary-content ps-10 pb-5 pe-10 w-auto"> 
  <pre data-prefix="$"><code>./aboutMe</code></pre> 
  <br />
  <pre className=""><code>Name: Amy Nguyen</code></pre>
  <br />
  <pre className=""><code>Age: 21</code></pre>
  <br />
  <pre className=""><code>School: University of Georgia</code></pre>
  <br />
  <pre className=""><code>Hometown: Lilburn, GA</code></pre>


</div>
      </div>
    </div>
  );
}
